import React, { useState } from "react";
import axios from "axios";
import { Container, Form, Button, Alert, Row, Col, Card, Spinner } from "react-bootstrap";
import "bootstrap/dist/css/bootstrap.min.css";

const App = () => {
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [DOB, setDOB] = useState("");
  const [phone, setPhone] = useState("");
  const [gender, setGender] = useState("");
  const [responseMessage, setResponseMessage] = useState("");
  const [showError, setShowError] = useState(false);
  const [data, setData] = useState([]);
  const [showData, setShowData] = useState(false); // To toggle between login and data view
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [loading, setLoading] = useState(false);
  const [isAuthenticated, setIsAuthenticated] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post("http://localhost:5000/submit-form", {
        name,
        email,
        DOB,
        phone,
        gender,
      });
      setResponseMessage(response.data);
      setShowError(false);
    } catch (error) {
      setResponseMessage("Error submitting data");
      setShowError(true);
    }
  };

  const handleClear = () => {
    setName("");
    setEmail("");
    setPhone("");
    setDOB("");
    setGender("");
    setResponseMessage("");
    setShowError(false);
  };

  const handleLogin = async (e) => {
    e.preventDefault();
    setLoading(true);
    try {
      const response = await axios.post("http://localhost:5000/admin-login", {
        username,
        password,
      });
      setIsAuthenticated(true);
      setResponseMessage(response.data);
      setShowError(false);
      setLoading(false);
    } catch (error) {
      setResponseMessage("Invalid credentials");
      setShowError(true);
      setLoading(false);
    }
  };

  const handleGetData = async () => {
    try {
      const response = await axios.get("http://localhost:5000/get-data");
      setData(response.data);
    } catch (error) {
      setResponseMessage("Error fetching data");
      setShowError(true);
    }
  };

  const handleCloseData = () => {
    setShowData(false);
  };

  return (
    <div style={{ backgroundColor: "black", minHeight: "100vh", padding: "20px" }}>
      <Container
        className="mt-5 p-4"
        style={{
          backgroundColor: "#ffffff", 
          borderRadius: "8px", 
          boxShadow: "0 4px 8px rgba(0, 0, 0, 0.1)", 
          maxWidth: "500px", 
          margin: "0 auto", 
        }}
      >
        {!isAuthenticated ? (
          <div>
            <h1 className="text-center mb-4">Admin Login</h1>
            <Form onSubmit={handleLogin}>
              <Form.Group controlId="formUsername">
                <Form.Label className="fw-bold">Username:</Form.Label>
                <Form.Control
                  type="text"
                  placeholder="Enter your username"
                  value={username}
                  onChange={(e) => setUsername(e.target.value)}
                  required
                />
              </Form.Group>

              <Form.Group controlId="formPassword" className="mt-3">
                <Form.Label className="fw-bold">Password :</Form.Label>
                <Form.Control
                  type="password"
                  placeholder="Enter your password"
                  value={password}
                  onChange={(e) => setPassword(e.target.value)}
                  required
                />
              </Form.Group>

              <Row className="d-flex justify-content-center mt-3">
                <Col xs="auto">
                  <Button variant="primary" type="submit">
                    {loading ? <Spinner animation="border" size="sm" /> : "Login"}
                  </Button>
                </Col>
              </Row>
            </Form>
            {responseMessage && (
              <Alert variant={showError ? "danger" : "success"} className="mt-3">
                {responseMessage}
              </Alert>
            )}
          </div>
        ) : (
          <div>
            <h1 className="text-center mb-4">Form Submission</h1>
            <Form onSubmit={handleSubmit}>
              {/* Form Fields for Form Submission */}
            </Form>

            {responseMessage && (
              <Alert variant={showError ? "danger" : "success"} className="mt-3">
                {responseMessage}
              </Alert>
            )}

            <Row className="mt-5">
              <Col className="d-flex justify-content-center">
                <Button variant="dark" onClick={handleGetData}>
                  Get Data
                </Button>
              </Col>
            </Row>

            {data.length > 0 && (
              <Row className="mt-4">
                {data.map((item, index) => (
                  <Col key={index} xs={12} sm={6} md={4} className="mb-4">
                    <Card>
                      <Card.Body>
                        <Card.Title>{item.name}</Card.Title>
                        <Card.Text><strong>Email:</strong> {item.email}</Card.Text>
                        <Card.Text><strong>Date of Birth:</strong> {item.DOB}</Card.Text>
                        <Card.Text><strong>Phone:</strong> {item.phone}</Card.Text>
                        <Card.Text><strong>Gender:</strong> {item.gender}</Card.Text>
                      </Card.Body>
                    </Card>
                  </Col>
                ))}
              </Row>
            )}

            <Row className="d-flex justify-content-center mt-3">
              <Col xs="auto">
                <Button variant="danger" onClick={handleCloseData}>
                  Close Data
                </Button>
              </Col>
            </Row>
          </div>
        )}
      </Container>
    </div>
  );
};

export default App;
