import React, { useState } from "react";
import axios from "axios";
import { Container, Form, Button, Alert, Row, Col } from "react-bootstrap";
import "bootstrap/dist/css/bootstrap.min.css";

const App = () => {
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [DOB, setDOB] = useState("");
  const [responseMessage, setResponseMessage] = useState("");
  const [showError, setShowError] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post("http://localhost:5000/submit-form", {
        name,
        email,
      });
      setResponseMessage(response.data);
      setShowError(false);
    } catch (error) {
      setResponseMessage("Error submitting data");
      setShowError(true);
    }
  };

  const handleClear = () => {
    setName("");
    setEmail("");
    setDOB("");
    setResponseMessage("");
    setShowError(false);
  };

  return (
    <div style={{ backgroundColor: "black", minHeight: "100vh", padding: "20px" }}>
    <Container
      className="mt-5 p-4"
      style={{
        backgroundColor: "#ffffff", // White background for the container
        borderRadius: "8px", // Rounded corners for the container
        boxShadow: "0 4px 8px rgba(0, 0, 0, 0.1)", // Subtle shadow to create a floating effect
        maxWidth: "500px", // Limiting max width of the form for a better look on larger screens
        margin: "0 auto", // Centering the container on the page
      }}
    >
      <h1 className="text-center mb-4">Form Submission</h1>
      <Form onSubmit={handleSubmit}>
        <Form.Group controlId="formName">
          <Form.Label>Name</Form.Label>
          <Form.Control
            type="text"
            placeholder="Enter your name"
            value={name}
            onChange={(e) => setName(e.target.value)}
            required
          />
        </Form.Group>

        <Form.Group controlId="formEmail" className="mt-3">
          <Form.Label>Email</Form.Label>
          <Form.Control
            type="email"
            placeholder="Enter your email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
        </Form.Group>
        <Form.Group className="mb-3" controlId="formDob">
                  <Form.Label className="fw-bold">Date of Birth :</Form.Label>
                  <Form.Control
                    type="date"
                    value={dob}
                    onChange={(e) => setDob(e.target.value)}
                    required
                    className="w-75 mx-auto mb-2 shadow-sm"
                  />
                  <Form.Control.Feedback type="invalid">
                    Please select your date of birth.
                  </Form.Control.Feedback>
                </Form.Group>

        <Row className="d-flex justify-content-center mt-3">
          <Col xs="auto">
            <Button variant="primary" type="submit">
              Submit
            </Button>
          </Col>
          <Col xs="auto">
            <Button variant="secondary" type="button" onClick={handleClear}>
              Clear
            </Button>
          </Col>
        </Row>
      </Form>

      {responseMessage && (
        <Alert variant={showError ? "danger" : "success"} className="mt-3">
          {responseMessage}
        </Alert>
      )}
    </Container>
    </div>
  );
};

export default App;
