import React, { useState } from "react";
import axios from "axios";
import { Container, Form, Button, Alert, Row, Col, Card, Spinner } from "react-bootstrap";
import "bootstrap/dist/css/bootstrap.min.css";

// LoginForm Component
const LoginForm = ({ onLoginSuccess }) => {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [loading, setLoading] = useState(false);
  const [responseMessage, setResponseMessage] = useState("");
  const [showError, setShowError] = useState(false);

  const handleLogin = async (e) => {
    e.preventDefault();
    setLoading(true);
    try {
      const response = await axios.post("http://localhost:5000/admin-login", {
        username,
        password,
      });
      setResponseMessage(response.data);
      setShowError(false);
      onLoginSuccess();
      setLoading(false);
    } catch (error) {
      setResponseMessage("Invalid credentials");
      setShowError(true);
      setLoading(false);
    }
  };

  return (
    <Container
      className="mt-5 p-4"
      style={{
        backgroundColor: "#ffffff",
        borderRadius: "8px",
        boxShadow: "0 4px 8px rgba(0, 0, 0, 0.1)",
        maxWidth: "500px",
        margin: "0 auto",
      }}
    >
      <h1 className="text-center mb-4">Admin Login</h1>
      <Form onSubmit={handleLogin}>
        <Form.Group controlId="formUsername">
          <Form.Label className="fw-bold">Username:</Form.Label>
          <Form.Control
            type="text"
            placeholder="Enter your username"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            required
          />
        </Form.Group>

        <Form.Group controlId="formPassword" className="mt-3">
          <Form.Label className="fw-bold">Password :</Form.Label>
          <Form.Control
            type="password"
            placeholder="Enter your password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
        </Form.Group>

        <Row className="d-flex justify-content-center mt-3">
          <Col xs="auto">
            <Button variant="primary" type="submit">
              {loading ? <Spinner animation="border" size="sm" /> : "Login"}
            </Button>
          </Col>
        </Row>
      </Form>
      {responseMessage && (
        <Alert variant={showError ? "danger" : "success"} className="mt-3">
          {responseMessage}
        </Alert>
      )}
    </Container>
  );
};

// DataView Component (For displaying the data after successful login)
const DataView = ({ data, handleCloseData }) => {
  return (
    <Container
      className="mt-5 p-4"
      style={{
        backgroundColor: "#ffffff",
        borderRadius: "8px",
        boxShadow: "0 4px 8px rgba(0, 0, 0, 0.1)",
        maxWidth: "1000px",
        margin: "0 auto",
      }}
    >
      <Row className="mt-4">
        {data.map((item, index) => (
          <Col key={index} xs={12} sm={6} md={4} className="mb-4">
            <Card>
              <Card.Body>
                <Card.Title>{item.name}</Card.Title>
                <Card.Text>
                  <strong>Email:</strong> {item.email}
                </Card.Text>
                <Card.Text>
                  <strong>Date of Birth:</strong> {item.DOB}
                </Card.Text>
                <Card.Text>
                  <strong>Phone:</strong> {item.phone}
                </Card.Text>
                <Card.Text>
                  <strong>Gender:</strong> {item.gender}
                </Card.Text>
              </Card.Body>
            </Card>
          </Col>
        ))}
      </Row>

      <Row className="d-flex justify-content-center mt-3">
        <Col xs="auto">
          <Button variant="danger" onClick={handleCloseData}>
            Close Data
          </Button>
        </Col>
      </Row>
    </Container>
  );
};

// Main App Component
const App = () => {
  const [data, setData] = useState([]);
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [showData, setShowData] = useState(false);

  const handleLoginSuccess = () => {
    setIsAuthenticated(true);
    setShowData(true);
  };

  const handleGetData = async () => {
    try {
      const response = await axios.get("http://localhost:5000/get-data");
      setData(response.data);
    } catch (error) {
      console.error("Error fetching data", error);
    }
  };

  const handleCloseData = () => {
    setShowData(false);
    setIsAuthenticated(false);
  };

  return (
    <div style={{ backgroundColor: "black", minHeight: "100vh", padding: "20px" }}>
      {!isAuthenticated ? (
        <LoginForm onLoginSuccess={handleLoginSuccess} />
      ) : (
        <div>
          <h1 className="text-center mb-4">Admin Dashboard</h1>
          <Row className="d-flex justify-content-center mt-3">
            <Col xs="auto">
              <Button variant="dark" onClick={handleGetData}>
                Get Data
              </Button>
            </Col>
          </Row>

          {showData && <DataView data={data} handleCloseData={handleCloseData} />}
        </div>
      )}
    </div>
  );
};

export default App;
